import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { WorkflowService } from '../workflow.service';
import { HotelService } from '../hotel.service';

@Component({
  selector: 'app-resume',
  templateUrl: './resume.component.html',
  styleUrls: ['./resume.component.css']
})
export class ResumeComponent {
  id = 0 as number ;
  flight: any;
  hotel: any;
  totalCost: number = 0;

  //activities: string[]; // List of activities

  constructor(private route: ActivatedRoute,private http: HttpClient,private workflowService : WorkflowService, private router: Router,private hotelService : HotelService,private flightService : Fli) { }

  ngOnInit() {
    this.route.paramMap.subscribe((params: { get: (arg0: string) => any; }) => {
      this.id = params.get('id'); 
    });
    this.getFlight(this.id);
    this.getHotel(this.id);
    console.log(this.flight,this.hotel);

    //this.activities = this.getActivities(); // Populate with actual activities
    this.calculateTotalCost();

  }

  getFlight(userId: number) {
    this.http.get(`http://localhost:8089/flights/flight/${userId}`)
      .subscribe(data => {
        this.flight = data;
        console.log(data);
      }, error => {
        console.error('Error fetching flight data', error);
      });
  }

  getHotel(userId: number) {
    this.http.get(`http://localhost:8089/hotels/hotel/${userId}`)
      .subscribe(data => {
        this.hotel = data;
        console.log(data);
      }, error => {
        console.error('Error fetching hotel data', error);
      });
  }
  findFlightObjectById(id){
    console.log(this.reclamService.findById(id));

    return this.reclamService.findById(id);
}
  getActivities(): string[] {
    // Ideally, these activities should be fetched from the backend or based on the country visited.
    return ['Activity 1', 'Activity 2', 'Activity 3'];
  }

  confirmAndNavigate() {
    // Logic to perform before navigation (if any)

    // Navigate to another page, e.g., '/confirmation'
    this.router.navigate(['/confirmation',this.id]);
  }
  calculateTotalCost() {
    this.totalCost = 0;
    console.log(this.flight.price);

    this.totalCost += this.flight.price;
    this.totalCost += this.hotel.price;

  }

}
