import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
@Component({
  selector: 'app-result',
  templateUrl: './result.component.html',
  styleUrls: ['./result.component.css']
})
export class ResultComponent implements OnInit {
  resultData: any;

  constructor(private route: ActivatedRoute) {}

  ngOnInit() {
    this.resultData = history.state.data;
    console.log('Result Data received from the submit:', this.resultData);
  }
}
<div class="result-container">
  <h2>Result Page</h2>
  <div class="result-info">
    <p><strong>Text:</strong> {{ resultData['@text'] }}</p>
    <p><strong>Confidence:</strong> {{ resultData['@confidence'] }}</p>
  </div>

  <div *ngIf="resultData.Resources && resultData.Resources.length > 0" class="resources">
    <h3>Resources</h3>
    <ul>
      <li *ngFor="let resource of resultData.Resources" class="resource-item">
        <p><strong>URI:</strong> {{ resource['@URI'] }}</p>
        <p><strong>Support:</strong> {{ resource['@support'] }}</p>
        <p><strong>Types:</strong> {{ resource['@types'] }}</p>
      </li>
    </ul>
  </div>
</div>
