import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'semantic';
  enteredText: string = '';

  submitText() {
    // Implement your logic here, such as handling the entered text
    console.log('Entered text:', this.enteredText);
    // You can perform further actions like API calls or navigation based on the entered text
  }
}
<div class="welcome-container">
  <h1>Welcome to Our Site</h1>
  <div class="input-container">
    <input  type="text" placeholder="Enter your text here" [(ngModel)]="enteredText" />
    <button (click)="submitText()">Submit</button>
  </div>
</div>
