import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-flight-search',
  templateUrl: './flight-search.component.html',
  styleUrls: ['./flight-search.component.css']
})
export class FlightSearchComponent {

  resultData: any = {};
  // Initialize departureDate to today's date
  resource = { departureDate: new Date().toISOString().split('T')[0] };

  constructor(private http: HttpClient) {}

  confirm(resource: any) {
    const fromId = 'BOM.AIRPORT'; // Replace with your fromId value
    const toId = 'DEL.AIRPORT'; // Replace with your toId value

    const url = `http://localhost:8080/flights/getMinFlightPrice`;
    const params = {
      fromId: fromId,
      toId: toId,
      departDate: resource.departureDate,
      currencyCode: 'AED' // Optional, you can adjust this value accordingly
    };

    this.http.get<any>(url, { params }).subscribe(
      (response) => {
        this.resultData = response;
        console.log('Flight search result:', this.resultData);
        // Process the response data as needed
      },
      (error) => {
        console.error('Error occurred:', error);
        // Handle error responses
      }
    );
  }
}
